import React, { Component } from 'react';
import axios from 'axios';
class NewsSlider extends Component {

    state = {
        news: []
    }

    componentWillMount() {
        axios.get(`http://localhost:3004/articles?_start=0&_end=3`)
            .then(response => {
                this.setState({
                    news: response.data
                })
            })
    }

    render() {

        return (
            <div>
                <SliderTemplates />
            </div>
        );
    }
}

export default NewsSlider;
