import React, { Component } from 'react';
import styles from './videosList.css';
import axios from 'axios';

import { URL } from '../../../config';
import Button from '../Buttons/buttons';
import VideosListTemplate from './videosListTemplate';
import { firebaseTeams, firebaseVideos, firebaseLooper } from '../../../firebase';

class VideosList extends Component {

    state = {
        teams: [],
        videos: [],
        start: this.props.start,
        end: this.props.start + this.props.amount,
        amount: this.props.amount
    }

    renderTitle() {
        return this.props.title ?
            <h3><strong>Cricket Videos</strong></h3>
            : null
    }

    componentWillMount() {
        this.request(this.state.start, this.state.end);
    }

    request = (start, end) => {
        if (this.state.teams.length < 1)
            firebaseTeams.once('value')
                .then((snapshot) => {
                    const teams = firebaseLooper(snapshot);
                    this.setState({
                        teams
                    })
                })

        firebaseVideos.orderByChild("id").startAt(start).endAt(end).once('value')
            .then((snapshot) => {
                const videos = firebaseLooper(snapshot);
                this.setState({
                    videos: [...this.state.items, ...videos],
                    start,
                    end
                })
            })
    }

    renderVideos = () => {
        let template = null;

        switch (this.props.type) {
            case ('card'):
                template = <VideosListTemplate
                    data={this.state.videos} teams={this.state.teams}
                />
                break;
            default:
                template = null
        }
        return template;

    }

    loadMore = () => {
        let end = this.state.end + this.state.amount;
        this.request(this.state.end, end)
    }

    renderButton() {
        return this.props.loadmore ?
            <Button
                type="loadmore"
                loadMore={() => this.loadMore()}
                cta="Load More Videos"
            />
            :
            <Button type="linkTo" cta="More Videos" linkTo="/videos" />
    }

    render() {
        return (
            <div className={styles.videosList_wrapper}>
                {this.renderTitle()}
                {this.renderVideos()}
                {this.renderButton()}
            </div>
        );
    }
}

export default VideosList;
